import React, { useState } from 'react';

function App() {
  const [input, setInput] = useState('');
  const [flashcards, setFlashcards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await fetch('http://localhost:5000/generate_flashcards', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ text: input })
      });
      const data = await res.json();
      setFlashcards(data.flashcards);
    } catch (err) {
      setError('Errore di connessione con il backend.');
      setFlashcards([]);
    }
    setLoading(false);
  }

  return (
    <div style={{maxWidth: "600px", margin: "auto", padding: "2rem"}}>
      <h1>Generatore di Flashcard AI</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          style={{width: "100%", minHeight: "80px"}}
          value={input}
          placeholder="Inserisci testo o argomento..."
          onChange={e => setInput(e.target.value)}
        />
        <button type="submit" disabled={loading || !input} style={{marginTop: "1rem"}}>
          {loading ? 'Generazione...' : 'Genera Flashcard'}
        </button>
      </form>
      {error && <div style={{color: "red"}}>{error}</div>}
      <div>
        {flashcards.length > 0 && (
          <h2 style={{marginTop: "2rem"}}>Flashcard generate:</h2>
        )}
        {flashcards.map((card, i) => (
          <div key={i} style={{
            border: "1px solid #ddd",
            borderRadius: "8px",
            padding: "1rem",
            margin: "1rem 0",
            boxShadow: "0 2px 4px rgba(0,0,0,0.04)"
          }}>
            <strong>Domanda:</strong>
            <div>{card.question}</div>
            <strong>Risposta:</strong>
            <div>{card.answer}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
