import React, { useState } from 'react';
import { FaSyncAlt, FaArrowRight, FaArrowLeft } from "react-icons/fa";
import './App.css';

function FlipCard({ question, answer }) {
  const [flipped, setFlipped] = useState(false);
  return (
    <div className={`flip-card${flipped ? " flipped" : ""}`} onClick={() => setFlipped(f => !f)}>
      <div className="flip-card-inner">
        <div className="flip-card-front">
          <div className="card-label">Domanda</div>
          <div className="card-content">{question}</div>
          <div className="card-tip">Clicca per vedere la risposta</div>
        </div>
        <div className="flip-card-back">
          <div className="card-label">Risposta</div>
          <div className="card-content">{answer}</div>
          <div className="card-tip">Clicca per tornare</div>
        </div>
      </div>
    </div>
  );
}

function Loader() {
  return (
    <div className="loader">
      <span>ðŸ¤–</span>
      <span>Generazione flashcard in corso...</span>
    </div>
  );
}

function App() {
  const [input, setInput] = useState('');
  const [flashcards, setFlashcards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [current, setCurrent] = useState(0);

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');
    setFlashcards([]);
    setCurrent(0);
    try {
      const res = await fetch('http://localhost:5000/generate_flashcards', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: input })
      });
      const data = await res.json();
      if (data.flashcards && Array.isArray(data.flashcards) && data.flashcards.length > 0) {
        setFlashcards(data.flashcards);
      } else {
        setError("Nessuna flashcard generata.");
      }
    } catch (err) {
      setError('Errore di connessione col backend.');
    }
    setLoading(false);
  }

  function nextCard() {
    setCurrent(c => Math.min(flashcards.length - 1, c + 1));
  }
  function prevCard() {
    setCurrent(c => Math.max(0, c - 1));
  }

  function handleReset() {
    setInput('');
    setFlashcards([]);
    setError('');
    setCurrent(0);
  }

  return (
    <div className="root-container">
      <h1 className="title">
        <span>ðŸŽ“</span> Generatore di Flashcard AI
      </h1>

      <form className="input-form" onSubmit={handleSubmit}>
        <textarea
          className="text-area"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Inserisci qui il testo o l'argomento da studiare..."
        />
        <button type="submit" disabled={loading || !input.trim()} className="gen-btn">
          {loading ? <Loader/> : <><FaSyncAlt/> Genera Flashcard</>}
        </button>
        {(flashcards.length > 0 || error) && (
          <button type="button" className="reset-btn" onClick={handleReset}>Reset</button>
        )}
      </form>

      {error && <div className="error-msg">{error}</div>}

      {flashcards.length > 0 && !loading && (
        <div className='flashcard-section'>
          <div className="navigation">
            <button onClick={prevCard} disabled={current === 0}><FaArrowLeft/></button>
            <span>{current + 1} / {flashcards.length}</span>
            <button onClick={nextCard} disabled={current === (flashcards.length - 1)}><FaArrowRight/></button>
          </div>
          <FlipCard
            question={flashcards[current].question}
            answer={flashcards[current].answer}
          />
          <div className="hint">Clicca sulla flashcard per flip!</div>
        </div>
      )}
    </div>
  );
}

export default App;
